import {useState,useEffect} from 'react';

import Bloglist from './Bloglist';
const Home = () => {
  const [blogs,setBlogs] = useState([
    {title:'My new website',body:'lorem ipsum...',auther: 'mario',id:1},
    {title:'Welcome Party!',body:'lorem ipsum...',auther: 'HK',id:2},
    {title:'wevdev top tips',body:'lorem ipsum...',auther: 'mario',id:3}
  ]);
   const [namm, setName] = useState('harekrushna');
  const handleDelete =(id) => {
    const newBlogs = blogs.filter(blog => blog.id !== id);
    setBlogs(newBlogs)
  }
  useEffect(() => {
    console.log('use effect ran');
    console.log(blogs);
  }, [namm]);
  // const [nam, setName] = useState('mario');
